package chapter01;

public class Relation {

	public static void main(String[] args) {
		
		int a = 10;
		int b = 3;
		
		System.out.println( 10 > 3 );
		System.out.println( 10 < 3 );
		System.out.println( 10 >= 3 );
		System.out.println( 10 <= 3 );
		System.out.println( 10 == 3 );
		System.out.println( 10 != 3 );
		
		System.out.println( 10 > 3 && 10 < 3); // and 연산자 
		System.out.println( 10 > 3 || 10 <3 ); // or 연산자
		System.out.println( !(10>3 ));
		
		System.out.println( 7 == 9 );
	    System.out.println();

	}

}
